// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";
import "./IBridgeGateway.sol";

/**
 * @title EVMGateway
 * @notice Simplified gateway - only locks/releases assets, coordinator handles proof verification
 * @dev Removed duplicate proof verification logic - coordinator is source of truth
 * 
 */
abstract contract EVMGateway is IBridgeGateway, ReentrancyGuard, Pausable, AccessControl {
    using SafeERC20 for IERC20;

    // ============ Constants ============

    bytes32 public constant COORDINATOR_ROLE = keccak256("COORDINATOR_ROLE");
    bytes32 public constant PAUSER_ROLE = keccak256("PAUSER_ROLE");

    address public constant NATIVE_ASSET = address(0);
    uint256 public constant MIN_DEPOSIT = 0.001 ether;
    uint256 public constant MAX_DEPOSIT = 1000000 ether;

    uint256 public bridgeFee = 30; // 0.3% in basis points

    // ============ State Variables ============

    address public coordinator;
    uint256 public depositNonce;

    // Token => locked balance
    mapping(address => uint256) public lockedBalances;

    // Nullifier => used (coordinator verifies, we just track)
    mapping(bytes32 => bool) public usedNullifiers;

    // Deposit ID => Deposit info
    mapping(bytes32 => DepositInfo) public deposits;

    // Statistics
    uint256 public totalDeposits;
    uint256 public totalWithdrawals;
    uint256 public totalVolume;

    // ============ Structs ============

    struct DepositInfo {
        address sender;
        address token;
        uint256 amount;
        uint64 targetChainId;
        bytes32 recipient;
        bytes32 zcashAddress;
        uint256 timestamp;
        bool processed;
    }

    // ============ Events ============

    event BridgeFeeUpdated(uint256 oldFee, uint256 newFee);

    // ============ Modifiers ============

    modifier onlyCoordinator() {
        require(hasRole(COORDINATOR_ROLE, msg.sender), "Not coordinator");
        _;
    }

    modifier validAmount(uint256 amount) {
        require(
            amount >= MIN_DEPOSIT && amount <= MAX_DEPOSIT,
            "Amount out of range"
        );
        _;
    }

    // ============ Constructor ============

    constructor(address _coordinator) {
        require(_coordinator != address(0), "Invalid coordinator");

        coordinator = _coordinator;

        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _grantRole(COORDINATOR_ROLE, _coordinator);
        _grantRole(PAUSER_ROLE, msg.sender);
    }

    // ============ Core Functions ============

    /**
     * @notice Deposit tokens for bridging
     * @dev Only locks tokens and emits event - coordinator handles Zcash note creation
     */
    function deposit(DepositRequest calldata request)
        external
        payable
        override
        nonReentrant
        whenNotPaused
        validAmount(request.amount)
        returns (bytes32 depositId)
    {
        require(request.targetChainId != block.chainid, "Cannot bridge to same chain");
        require(request.zcashAddress != bytes32(0), "Invalid Zcash address");

        // Calculate fee
        uint256 fee = (request.amount * bridgeFee) / 10000;
        uint256 netAmount = request.amount - fee;

        // Handle native asset vs ERC20
        if (request.token == NATIVE_ASSET) {
            require(msg.value == request.amount, "Incorrect ETH amount");
        } else {
            require(msg.value == 0, "ETH not accepted for ERC20");
            IERC20(request.token).safeTransferFrom(
                msg.sender,
                address(this),
                request.amount
            );
        }

        // Generate unique deposit ID
        depositId = keccak256(abi.encodePacked(
            msg.sender,
            request.token,
            request.amount,
            request.targetChainId,
            request.recipient,
            request.zcashAddress,
            depositNonce++,
            block.number,
            block.timestamp,
            blockhash(block.number - 1)
        ));

        // Store deposit info
        deposits[depositId] = DepositInfo({
            sender: msg.sender,
            token: request.token,
            amount: netAmount,
            targetChainId: request.targetChainId,
            recipient: request.recipient,
            zcashAddress: request.zcashAddress,
            timestamp: block.timestamp,
            processed: false
        });

        // Update balances
        lockedBalances[request.token] += netAmount;
        totalDeposits += netAmount;
        totalVolume += netAmount;

        emit TokensLocked(
            depositId,
            msg.sender,
            request.token,
            netAmount,
            request.targetChainId,
            request.recipient,
            request.zcashAddress,
            block.timestamp
        );

        return depositId;
    }

    /**
     * @notice Withdraw tokens - ONLY callable by coordinator after proof verification
     * @dev Coordinator verifies Zcash proof off-chain, then calls this to release tokens
     * @dev Removed on-chain proof verification - coordinator is trusted authority
     */
    function withdraw(WithdrawalRequest calldata request)
        external
        override
        nonReentrant
        whenNotPaused
        onlyCoordinator
        returns (bool)
    {
        require(!usedNullifiers[request.nullifier], "Nullifier already used");
        require(request.amount > 0, "Invalid amount");
        require(
            lockedBalances[request.token] >= request.amount,
            "Insufficient locked balance"
        );

        // Mark nullifier as used
        usedNullifiers[request.nullifier] = true;

        // Update balances
        lockedBalances[request.token] -= request.amount;
        totalWithdrawals += request.amount;

        // Generate withdrawal ID
        bytes32 withdrawalId = keccak256(abi.encodePacked(
            request.recipient,
            request.token,
            request.amount,
            request.nullifier,
            block.timestamp
        ));

        // Transfer tokens
        if (request.token == NATIVE_ASSET) {
            (bool success, ) = payable(request.recipient).call{value: request.amount}("");
            require(success, "ETH transfer failed");
        } else {
            IERC20(request.token).safeTransfer(request.recipient, request.amount);
        }

        emit TokensReleased(
            withdrawalId,
            request.recipient,
            request.token,
            request.amount,
            request.nullifier,
            block.timestamp
        );

        return true;
    }

    // ============ View Functions ============

    function getLockedBalance(address token)
        external
        view
        override
        returns (uint256)
    {
        return lockedBalances[token];
    }

    function getAvailableLiquidity(address token)
        external
        view
        override
        returns (uint256)
    {
        if (token == NATIVE_ASSET) {
            return address(this).balance;
        }
        return IERC20(token).balanceOf(address(this));
    }

    function isNullifierUsed(bytes32 nullifier)
        external
        view
        override
        returns (bool)
    {
        return usedNullifiers[nullifier];
    }

    function getDeposit(bytes32 depositId)
        external
        view
        override
        returns (
            bool exists,
            address sender,
            address token,
            uint256 amount,
            uint64 targetChainId,
            bool processed
        )
    {
        DepositInfo memory info = deposits[depositId];
        return (
            info.timestamp > 0,
            info.sender,
            info.token,
            info.amount,
            info.targetChainId,
            info.processed
        );
    }

    function getStats()
        external
        view
        override
        returns (BridgeStats memory)
    {
        return BridgeStats({
            totalDeposits: totalDeposits,
            totalWithdrawals: totalWithdrawals,
            totalVolume: totalVolume,
            activeDeposits: totalDeposits - totalWithdrawals
        });
    }

    // ============ Admin Functions ============

    function setCoordinator(address newCoordinator)
        external
        override
        onlyRole(DEFAULT_ADMIN_ROLE)
    {
        require(newCoordinator != address(0), "Invalid coordinator");

        address oldCoordinator = coordinator;

        _revokeRole(COORDINATOR_ROLE, oldCoordinator);
        _grantRole(COORDINATOR_ROLE, newCoordinator);

        coordinator = newCoordinator;

        emit CoordinatorUpdated(oldCoordinator, newCoordinator, block.timestamp);
    }


    function setPaused(bool paused)
        external
        override
        onlyRole(PAUSER_ROLE)
    {
        if (paused) {
            _pause();
        } else {
            _unpause();
        }
    }

    function setBridgeFee(uint256 newFee)
        external
        onlyRole(DEFAULT_ADMIN_ROLE)
    {
        require(newFee <= 100, "Fee too high"); // Max 1%
        uint256 oldFee = bridgeFee;
        bridgeFee = newFee;
        emit BridgeFeeUpdated(oldFee, newFee);
    }

    function emergencyWithdraw(
        address token,
        address to,
        uint256 amount
    )
        external
        override
        onlyRole(DEFAULT_ADMIN_ROLE)
        whenPaused
    {
        require(to != address(0), "Invalid recipient");

        if (token == NATIVE_ASSET) {
            (bool success, ) = payable(to).call{value: amount}("");
            require(success, "ETH transfer failed");
        } else {
            IERC20(token).safeTransfer(to, amount);
        }

        emit EmergencyPause(msg.sender, "Emergency withdrawal", block.timestamp);
    }

    // ============ Receive ETH ============

    receive() external payable {
        // Accept ETH deposits
    }
}

