// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

/**
 * @title IBridgeGateway
 * @notice Universal interface for ZeroBridge gateways across all EVM chains
 * @dev All gateway implementations (Ethereum, Base, Polygon) must implement this
 */
interface IBridgeGateway {

    // ============ Structs ============

    /**
     * @notice Deposit request structure
     * @param token Token address (address(0) for native asset)
     * @param amount Amount to bridge
     * @param targetChainId Destination chain ID
     * @param recipient Recipient address on destination chain (bytes32 for cross-VM)
     * @param zcashAddress Shielded address on Zcash for privacy
     */
    struct DepositRequest {
        address token;
        uint256 amount;
        uint64 targetChainId;
        bytes32 recipient;
        bytes32 zcashAddress;
    }

    /**
     * @notice Withdrawal request structure
     * @param recipient Address to receive tokens
     * @param token Token address
     * @param amount Amount to withdraw
     * @param nullifier Zcash nullifier (prevents double-spend)
     * @param zcashProof Halo2 proof from Zcash coordinator
     * @param merkleRoot Zcash merkle root at time of proof
     */
    struct WithdrawalRequest {
        address recipient;
        address token;
        uint256 amount;
        bytes32 nullifier;
        bytes zcashProof;
        bytes32 merkleRoot;
    }

    /**
     * @notice Bridge statistics
     */
    struct BridgeStats {
        uint256 totalDeposits;
        uint256 totalWithdrawals;
        uint256 totalVolume;
        uint256 activeDeposits;
    }

    // ============ Events ============

    /**
     * @notice Emitted when tokens are locked for bridging
     */
    event TokensLocked(
        bytes32 indexed depositId,
        address indexed sender,
        address indexed token,
        uint256 amount,
        uint64 targetChainId,
        bytes32 recipient,
        bytes32 zcashAddress,
        uint256 timestamp
    );

    /**
     * @notice Emitted when tokens are released after withdrawal
     */
    event TokensReleased(
        bytes32 indexed withdrawalId,
        address indexed recipient,
        address indexed token,
        uint256 amount,
        bytes32 nullifier,
        uint256 timestamp
    );

    /**
     * @notice Emitted when liquidity is added
     */
    event LiquidityAdded(
        address indexed provider,
        address indexed token,
        uint256 amount,
        uint256 timestamp
    );

    /**
     * @notice Emitted when liquidity is removed
     */
    event LiquidityRemoved(
        address indexed provider,
        address indexed token,
        uint256 amount,
        uint256 timestamp
    );

    /**
     * @notice Emitted when coordinator is updated
     */
    event CoordinatorUpdated(
        address indexed oldCoordinator,
        address indexed newCoordinator,
        uint256 timestamp
    );

    /**
     * @notice Emitted when emergency pause is triggered
     */
    event EmergencyPause(
        address indexed triggeredBy,
        string reason,
        uint256 timestamp
    );

    // ============ Core Functions ============

    /**
     * @notice Deposit tokens to bridge to another chain
     * @param request Deposit request details
     * @return depositId Unique identifier for this deposit
     */
    function deposit(DepositRequest calldata request) 
        external 
        payable 
        returns (bytes32 depositId);

    /**
     * @notice Withdraw tokens using Zcash proof
     * @param request Withdrawal request with proof
     * @return success Whether withdrawal succeeded
     */
    function withdraw(WithdrawalRequest calldata request) 
        external 
        returns (bool success);

    /**
     * @notice Get locked balance for a token
     * @param token Token address
     * @return balance Locked balance
     */
    function getLockedBalance(address token) 
        external 
        view 
        returns (uint256 balance);

    /**
     * @notice Get available liquidity for a token
     * @param token Token address
     * @return available Available liquidity
     */
    function getAvailableLiquidity(address token) 
        external 
        view 
        returns (uint256 available);

    /**
     * @notice Check if nullifier has been used
     * @param nullifier Zcash nullifier
     * @return used Whether nullifier is used
     */
    function isNullifierUsed(bytes32 nullifier) 
        external 
        view 
        returns (bool used);

    /**
     * @notice Get deposit details
     * @param depositId Deposit identifier
     * @return exists Whether deposit exists
     * @return sender Sender address
     * @return token Token address
     * @return amount Amount deposited
     * @return targetChainId Target chain
     * @return processed Whether processed
     */
    function getDeposit(bytes32 depositId) 
        external 
        view 
        returns (
            bool exists,
            address sender,
            address token,
            uint256 amount,
            uint64 targetChainId,
            bool processed
        );

    /**
     * @notice Get bridge statistics
     * @return stats Bridge statistics
     */
    function getStats() 
        external 
        view 
        returns (BridgeStats memory stats);

    // ============ Admin Functions ============

    /**
     * @notice Update Zcash coordinator address
     * @param newCoordinator New coordinator address
     */
    function setCoordinator(address newCoordinator) external;

    /**
     * @notice Add liquidity provider
     * @param provider Provider address
     */
    function addLiquidityProvider(address provider) external;

    /**
     * @notice Remove liquidity provider
     * @param provider Provider address
     */
    function removeLiquidityProvider(address provider) external;

    /**
     * @notice Pause/unpause contract
     * @param paused Pause state
     */
    function setPaused(bool paused) external;

    /**
     * @notice Emergency withdraw (only when paused)
     * @param token Token to withdraw
     * @param to Destination address
     * @param amount Amount to withdraw
     */
    function emergencyWithdraw(
        address token,
        address to,
        uint256 amount
    ) external;
}

